//Cookie Consent Management(function(){const COOKIE_NAME='cookieConsent';const COOKIE_EXPIRY_DAYS=365;//Check if user has already made a choice function getCookieConsent(){const value=localStorage.getItem(COOKIE_NAME);return value;}//Set cookie consent function setCookieConsent(accepted){localStorage.setItem(COOKIE_NAME,accepted?'accepted':'rejected');const expiryDate=new Date();expiryDate.setDate(expiryDate.getDate()+COOKIE_EXPIRY_DAYS);document.cookie=`${COOKIE_NAME}=${accepted?'accepted':'rejected'};expires=${expiryDate.toUTCString()};path=/;SameSite=Lax`;}//Show/Hide banner function showBanner(){const banner=document.getElementById('cookieConsent');if(banner){banner.style.display='flex';}}function hideBanner(){const banner=document.getElementById('cookieConsent');if(banner){banner.style.display='none';}}//Handle AdSense based on consent function handleAdSense(accepted){if(!accepted){//Disable AdSense if user rejected cookies if(window.adsbygoogle){window.adsbygoogle.pauseAdRequests=1;}}}//Initialize function init(){const consent=getCookieConsent();if(consent===null){//No consent given yet,show banner showBanner();}else{//Consent already given hideBanner();handleAdSense(consent==='accepted');}//Accept button const acceptBtn=document.getElementById('cookieAccept');if(acceptBtn){acceptBtn.addEventListener('click',function(){setCookieConsent(true);hideBanner();handleAdSense(true);//Reload to activate AdSense if(window.adsbygoogle){location.reload();}});}//Reject button const rejectBtn=document.getElementById('cookieReject');if(rejectBtn){rejectBtn.addEventListener('click',function(){setCookieConsent(false);hideBanner();handleAdSense(false);});}}//Run when DOM is ready if(document.readyState==='loading'){document.addEventListener('DOMContentLoaded',init);}else{init();}})();